import React from "react";
import { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/esm/Button";

const SetShifts = (props) => {
    const [show, setShow] = useState(false);

    function openModal() {
        setShow(true);
    }

    function closeModal() {
        setShow(false);
    }
    

    return (
        <div>
            <button onClick={openModal}>Set Shift</button>
            <Modal show={show}
            animation={true}>
                <Modal.Header >
                    <Modal.Title>Set Shift Assignment</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {props.getAllShiftsProperty &&
                    props.getAllShiftsProperty.map((shift, index) => {
                        return(
                            <tr>
                                <td>{shift.name}</td>
                                <td>{shift.start}</td>
                                <td>{shift.end}</td>
                                <td>{shift.qual_required}</td>
                                <td>{shift.nurse_id}</td>
                            </tr>
                )
                    }
                )}

                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={closeModal}>Save Assignment</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}
 
export default SetShifts;
