import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DisplayShifts from './components/DisplayShifts/DisplayShifts';
import ShiftTable from './components/DisplayShifts/ShiftTable/ShiftTable';
import Table from 'react-bootstrap/Table';

import './App.css';

function App() {
  const [shifts, setAllShifts] = useState();
  const [nurses, setAllNurses] = useState();

  useEffect(() => {
    const getShifts = async() => {
      try {
        let response = await axios.get('/shifts');
        console.log(response.data);
        setAllShifts(response.data);
      } catch (error) {
        console.log('Error with Getting All the Shifts!')
      }
    };
    const getNurses = async() => {
      try {
        let response = await axios.get('/nurses');
        console.log(response.data);
        setAllNurses(response.data);
      } catch (error) {
        console.log('Error with Getting All the Nurses!')
      }
    };
    getShifts(); 
    getNurses();
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <Fragment>
            {shifts &&
                shifts.map((shift, index) => {
                    return(
                        <Table striped bordered hover size="sm">
                            <thead>
                                <tr>
                                    <th>Shift</th>
                                    <th>Start Time</th>
                                    <th>End Time</th>
                                    <th>Certification Requiried</th>
                                    <th>Assigned Nurse</th>
                                </tr> 
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{shift.name}</td>
                                    <td>{shift.start}</td>
                                    <td>{shift.end}</td>
                                    <td>{shift.qual_required}</td>
                                    <td>{shift.nurse_id}</td>
                                </tr>
                            </tbody>
                        </Table>
                    )
                }
            )}
          </Fragment>
        </div>
      </header>
    </div>
  );
}

export default App;
